import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Inscribir } from 'src/app/Entidades/inscribir';
import { CursoService } from 'src/app/services/Curso/curso.service';

@Component({
  selector: 'app-catalogo',
  templateUrl: './catalogo.component.html',
  styleUrls: ['./catalogo.component.css']
})
export class CatalogoComponent {

  Inscribir: any = {
    idC:"" + localStorage.getItem("nombreC"),
    idE: "",
    fecha: '',
    id: 0
  };

  constructor(

    public CursosService: CursoService,
    private router:Router,
    private route:ActivatedRoute,
    //private toastController:ToastController,

    public fb: FormBuilder,
  ) {

  }
  ngOnInit(): void {

    this.CursosService.getAllCursos().subscribe((data)=>{
      this.Cursos=data
      console.log(data)
     },(error)=>{
      console.error(error)
     }
     );


  }
  // Calcular(id:number):void {
  //   localStorage.setItem("id",""+id)

  // console.log("entra",id)
  // this.router.navigate(["/Ruta3",id])
  // }


  Eliminar(Curso:any):void {
    console.log(Curso)
    this.CursosService.Elminar(Curso.idc).subscribe((data)=>{
       console.log(data)

        this.Cursos.pop(Curso)
    },(error)=>{
    console.error(error)
    }
    );
      }
}
