import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { CursoService } from 'src/app/services/Curso/curso.service';
import { EstudiateService } from 'src/app/services/Curso/estudiate.service';

@Component({
  selector: 'app-iniciar-sesion',
  templateUrl: './iniciar-sesion.component.html',
  styleUrls: ['./iniciar-sesion.component.css']
})
export class IniciarSesionComponent {
  loginForm : FormGroup =this.fb.group({
    email: ['', Validators.required],
    contrasenia: ['', Validators.required]})

  constructor(

    private router:Router,
    private route:ActivatedRoute,
    public fb: FormBuilder,
    public EstudianteService: EstudiateService
  ) {

  }
  ngOnInit(): void {
  }
  Iniciar() {
    if(!this.loginForm.valid){

      console.log('ingresa datos')
      return false;
    }else{
      this.EstudianteService.login(this.loginForm.value).subscribe(

        (data) =>{

          console.log('hola',data)
          this.router.navigate(["/Ruta4"])

      },(error)=>{

        console.error(error)
      })
      return true;
    }

  }
}


<div class="formularioRegistro">
    <h1 style="text-align:center">Crear Curso</h1>
    <form [formGroup]="loginForm" (ngSubmit)="Iniciar()">
        <div class="form-group">

            <label>Codigo Curso</label>
            <input matInput type="text" formControlName="codigoCurso" class="form-control">
        </div>
        <div class="form-group">

            <label>Descripcion</label>
            <input matInput type="text" formControlName="descripcion" class="form-control">
        </div>
        <div class="form-group">

            <label>Horas</label>
            <input matInput type="text" formControlName="horas" class="form-control">
        </div>
        <div class="form-group">

            <label>Fecha Inicio</label>
            <input matInput type="text" formControlName="fechaInicio" class="form-control">
        </div>
        <div class="form-group">

            <label>Fecha Fin</label>
            <input matInput type="text" formControlName="fechaFin" class="form-control">
        </div>
        <div class="form-group">

            <label>Cupos</label>
            <input matInput type="number" formControlName="cupos" class="form-control">
        </div>

        <div style="text-align:center" class="form-group">

            <button type="submit" class="btn btn-outline-success">Guardar</button>
        </div>


    </form>
</div>
