import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { CursoService } from 'src/app/services/Curso/curso.service';

@Component({
  selector: 'app-crear-curso',
  templateUrl: './crear-curso.component.html',
  styleUrls: ['./crear-curso.component.css']
})
export class CrearCursoComponent {
  CursoForm : FormGroup =this.fb.group({
    codigoCurso: ['', Validators.required],
    descripcion: ['', Validators.required],
    fechaInicio: ['', Validators.required],
    fechaFin: ['', Validators.required],
    fechaFin: ['', Validators.required],
    cupos: [0, Validators.required]

  })

  constructor(

    private router:Router,
    private route:ActivatedRoute,
    public fb: FormBuilder,
    public cursoService: CursoService
  ) {

  }
  ngOnInit(): void {



  }
  guardar() {
    if(!this.CursoForm.valid){

      console.log('ingresa datos')
      return false;
    }else{
      this.cursoService.saveCurso (this.CursoForm.value).subscribe(

        (data) =>{
          this.CursoForm.reset();
          console.log('hola',data)

      },(error)=>{

        console.error(error)
      })
      return true;
    }


  }

}
<div class="formularioRegistro">
    <h1 style="text-align:center">Crear Producto</h1>
    <form [formGroup]="CursoForm" (ngSubmit)="guardar()">
        <div class="form-group">

            <label>Nombre del Producto</label>
            <input matInput type="text" formControlName="codigoCurso" class="form-control">
        </div>
        <div class="form-group">

            <label>Tiempo de construcción en metalmecánica (minutos)</label>
            <input matInput type="text" formControlName="descripcion" class="form-control">
        </div>
        <div class="form-group">

            <label>Tiempo de galvanizado (minutos)</label>
            <input matInput type="text" formControlName="fechaInicio" class="form-control">
        </div>
        <div class="form-group">

            <label>Tiempo de armado (minutos)</label>
            <input matInput type="text" formControlName="fechaFin" class="form-control">
        </div>
        <div class="form-group">

            <label>Tiempo de armado (minutos)</label>
            <input matInput type="number" formControlName="cupos" class="form-control">
        </div>
        <div style="text-align:center" class="form-group">

            <button type="submit" class="btn btn-outline-success">Guardar</button>
        </div>


    </form>
</div>
