import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { CursoService } from '../../services/Curso/curso.service';
import { EstudiateService } from '../../services/Curso/estudiate.service';

@Component({
  selector: 'app-crear-estudiante',
  templateUrl: './crear-estudiante.component.html',
  styleUrls: ['./crear-estudiante.component.css']
})
export class CrearEstudianteComponent {
  EstudianteForm : FormGroup =this.fb.group({
    id: '',
    cedula: ['', Validators.required],
    nombre: ['', Validators.required],
    apellido: ['', Validators.required],
    email: ['', Validators.required],
    contrasenia: ['', Validators.required],
    rol: ['', Validators.required],
    telefono: [, Validators.required]

  })


  constructor(

    private router:Router,
    private route:ActivatedRoute,
    public fb: FormBuilder,
    public estudianteService: EstudiateService
  ) {

  }
  ngOnInit(): void {

    this.estraerPerfil()

  }
  guardar() {

    if(!this.EstudianteForm.valid){
      return false;
    }else{
      this.estudianteService.UpdateEstudiante(this.EstudianteForm.value).subscribe(

        (data) =>{

          console.log('hola',data)

      },(error)=>{

        console.error(error)
      })
      return true;
    }


  }

  estraerPerfil(){
    const numeroEntero = parseInt("" + localStorage.getItem("idC"), 10);
    this.estudianteService.getAllEtu(numeroEntero).subscribe((data) => {

      this.EstudianteForm.setValue({
      id:data.id,
      cedula:data.cedula,
      nombre:data.nombre,
      apellido:data.apellido,
      email:data.email,
      contrasenia:data.contrasenia,
      rol:data.rol,
      telefono:data.telefono

      })
      console.log(this.EstudianteForm.value)




    }, (error) => {
      console.error(error)
    }
    );
  }

}
<div class="formularioRegistro">
    <h1 style="text-align:center">Crear Estudiante</h1>
    <form [formGroup]="EstudianteForm" (ngSubmit)="guardar()">
        <div class="form-group">

            <label>Cedula</label>
            <input matInput type="text" formControlName="cedula" class="form-control">
        </div>
        <div class="form-group">

            <label>Nombre</label>
            <input matInput type="text" formControlName="nombre" class="form-control">
        </div>
        <div class="form-group">

            <label>Apellido</label>
            <input matInput type="text" formControlName="apellido" class="form-control">
        </div>
        <div class="form-group">

            <label>Email</label>
            <input matInput type="text" formControlName="email" class="form-control">
        </div>
        <div class="form-group">

            <label>Contrasenia</label>
            <input matInput type="text" formControlName="contrasenia" class="form-control">
        </div>
        <div class="form-group">

            <label>Rol</label>
            <input matInput type="number" formControlName="rol" class="form-control">
        </div>
        <div class="form-group">

            <label>telefono</label>
            <input matInput type="number" formControlName="telefono" class="form-control">
        </div>

        <div style="text-align:center" class="form-group">

            <button type="submit" class="btn btn-outline-success">Guardar</button>
        </div>


    </form>
</div>
